import styled from "styled-components";

export const Container = styled.nav`
    background: gray;
    position: fixed;
    top: 0;
    width: 100%;
    box-shadow: 0 4px 4px rgba(0, 0, 0, 0.25);
    padding:10px;
`
export const Content = styled.ul`
    display:flex;
    justify-content: center;
    align-items: center;
    list-style: none;

    h1 {
        font-size: 45px;
        color: var(--orange);

    }
    li{
        margin: 0 30px;
        cursor: pointer;
        height:50px;
        display: flex;
        align-items: center;
    }
    li:hover{
        border-bottom: 1px solid black;

    }

`
