import { Container } from "./style";
import { useContext } from "react";
import { BebidasContext } from "../../Providers/Bebidas";
import { ConfraternizacaoContext } from "../../Providers/Confraternização";
import { FormaturaContext } from "../../Providers/Formatura";
import { CasamentoContext } from "../../Providers/Casamento";

const Button = ({ children, blackSchema = false, type, item, ...rest }) => {
  const { bebidaCasamento, adicionarBebidaCasamento, removerBebidaCasamento } =
    useContext(CasamentoContext);

  const {
    bebidaConfraternizacao,
    adicionarBebidaConfraternizacao,
    removerConfraternizacao,
  } = useContext(ConfraternizacaoContext);

  const { bebidaFormatura, adicionarBebidaFormatura, removerFormatura } =
    useContext(FormaturaContext);

  const condicoes = (
    tipo,
    tipoCasamento,
    tipoFormatura,
    tipoConfraternizacao
  ) => {
    if (tipo === "bebidas") {
      tipoCasamento = "Casamento";
      tipoFormatura = "Formatura";
      tipoConfraternizacao = "Confraternização";
    }
    if (tipo === "casamento") {
      return "Remover";
    }
    if (tipo === "formatura") {
      return "Remover";
    }
    if (tipo === "confraternizacao") {
      return "Remover";
    }
  };

  const handleCLick = () => {
      if(condicoes("bebidas")){
        adicionarBebidaFormatura(item)
      }
  }

  return (
    <Container blackSchema={blackSchema} type="button" {...rest}>
      {children}
    </Container>
  );
};

export default Button;
