import Button from "../Button";
import { useContext } from "react";
import { BebidasContext } from "../../Providers/Bebidas";
import { CasamentoContext } from "../../Providers/Casamento";
import { ConfraternizacaoContext } from "../../Providers/Confraternização";
import { FormaturaContext } from "../../Providers/Formatura";

const ListaDeBebidas = ({
  tipo,
  tipoCasamento,
  tipoFormatura,
  tipoConfraternizacao,
}) => {
  const { bebidas } = useContext(BebidasContext);
  const { bebidaCasamento } = useContext(CasamentoContext);
  const { bebidaConfraternizacao } = useContext(ConfraternizacaoContext);
  const { bebidaFormatura } = useContext(FormaturaContext);

  const teste = () => {
    console.log("Clique")
  }

  return (
    <div>
      {/* Preciso de um tipo para adicionar a 3 componentes diferentes  */}
      {tipo === "bebidas" &&
        bebidas.map((item, index) => (
          <div key={index}>
            <img src={item.image_url} alt={item.name} />
            <h2>
              {item.name} <span>{item.volume.value}L</span>
            </h2>
            <span>{item.description}</span>
            <span>Fabricação: {item.first_brewed}</span>
            <Button handleClick ={teste()} tipo={tipo} item={item} />
            <Button handleClick tipo={tipo} item={item} />
            <Button handleClick tipo={tipo} item={item} />
          </div>
        ))}

      {/* Daqui para baixo o tipo será para remover */}
      {tipo === "casamento" &&
        bebidaCasamento.map((item, index) => (
          <div key={index}>
            <img src={item.image_url} alt={item.name} />
            <h2>
              {item.name} <span>{item.volume.value}L</span>
            </h2>
            <span>{item.description}</span>
            <span>Fabricação: {item.first_brewed}</span>
            <Button tipo={tipo} item={item} />
          </div>
        ))}
      {tipo === "confraternizacao" &&
        bebidaConfraternizacao.map((item, index) => (
          <div key={index}>
            <img src={item.image_url} alt={item.name} />
            <h2>
              {item.name} <span>{item.volume.value}L</span>
            </h2>
            <span>{item.description}</span>
            <span>Fabricação: {item.first_brewed}</span>
            <Button tipo={tipo} item={item} />
          </div>
        ))}

      {tipo === "formatura" &&
        bebidaFormatura.map((item, index) => (
          <div key={index}>
            <img src={item.image_url} alt={item.name} />
            <h2>
              {item.name} <span>{item.volume.value}L</span>
            </h2>
            <span>{item.description}</span>
            <span>Fabricação: {item.first_brewed}</span>
            <Button tipo={tipo} item={item} />
          </div>
        ))}
    </div>
  );
};
export default ListaDeBebidas;
