import styled from "styled-components";

export const Container = styled.div`
  background-color: #2f2e2f;
  width: 90%;
  height: 90vh;
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  box-shadow: 0 7px 7px rgba(0, 0, 0, 0.35);
`;

export const Content = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-around;
  flex-wrap: wrap;

  div {
    width: 45%;
    margin-left: 90px;
    line-height: 1;

    h1 {
      font-size:4.5rem;
      margin-bottom: 40px;
      letter-spacing: 5px;
      color: var(--orange);
    }
    p {
        margin-bottom: 40px;
        width: 70%;
        text-align: justify;
        color: var(--whiteCity);
    }

    button {
        width: 150px;
    }
  }

`;

export const Background = styled.div`
@media (min-width: 1100px){

    flex: 1;
   width: 45%;
   img{
       width: 100%;
   }


}



`
