import styled from "styled-components";

export const Container = styled.div`
  background: gray;
  width: 95%;
  max-width: 900px;
  margin: 0 auto;
  display: flex;
  flex-wrap: wrap;
  justify-content: space-evenly;
`;
export const Card = styled.div`
  background: tomato;
  width: 30%;
  display: flex;
  align-items: center;
  flex-direction: column;
  margin: 0.5rem;
  padding: 0.5rem;

  h2{
      margin-top: 1rem;;
  }
  div {
    display: flex;
    justify-content: space-between;
    width: 95%;
    div{
        width: 35%;
        display:flex;
        flex-direction: column;
        align-items: center;
    }
  }
  button {
      width: 100%;
    max-width: 50px;
  }
  img {
    width: 100px;
    height: 225px;
    margin-right: 30px;
  }
  details{
      text-align: center;
      font-size: 13px;
        cursor: pointer;
  }
`;
