import styled from "styled-components";

export const Container = styled.div`
  background: rgba(0,0,0,0.6);
  border-radius: 0.5rem;
  width: 95%;
  max-width: 1250px;
  margin: -460px auto;
  display: flex;
  flex-wrap: wrap;
  padding: 10px;
  align-items: flex-start ;
  text-align: center;
  justify-content: space-evenly;
`;
export const Card = styled.div`

  width: 31%;
  display: flex;
  align-items: center;
  flex-direction: column;
  margin: 0.5rem;
  padding: 1rem;
  background: var(--whiteCity);
  border-radius: 0.5rem;


  h2 {
    margin-top: 1rem;
    font-size: 16px;
  }
  div {
    display: flex;
    justify-content: space-between;
    width: 100%;
    div {
      width: 35%;
      display: flex;
      flex-direction: column;
      align-items: center;
    }
  }
  button {
    width: 100%;
    max-width: 50px;
  }
  img {
    width: 45%;
    height: 225px;
    @media (min-width: 1100px) {

    }
  }
  span {
      font-size: 10px;
  }
  details {
    text-align: center;
    font-size: 13px;
    cursor: pointer;
  }
`;
