import styled from "styled-components"

export const Container = styled.div`
    background-color: #2F2E2F;
    width: 90%;
    height: 90vh;
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    box-shadow: 0 7px 7px rgba(0, 0, 0, 0.35);
`

export  const Content = styled.div`
    display: flex;
    align-items: center;

    div{
        width: 50%;
    }
    figure {
        width: 50%;
    }
    figure img{
        width: 100%;
    }
`
