import styled from "styled-components"

export const Container = styled.div`
    background: gray;
    width: 95%;
    max-width: 900px;
    margin: 0 auto;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-evenly;

`
export const Card = styled.div`
        background: tomato;
        width:30%;
        display: flex;
        ;
        flex-direction: column;
        button{
            width: 50px;
        }
        img {
            width: 100px;
            height: 215px;
        }
`
